import java.util.Scanner;

public class Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int area,b,h;
		Scanner sc=new Scanner(System.in);
		b=sc.nextInt();
		h=sc.nextInt();
		area=(b*h)/2;
		System.out.println("Area of a Triangle is:"+area);
	
	}

}
